INSERT INTO `wp_wpdatatables` VALUES (17,'MeetingDashboard',0,'mysql','','SELECT \nmu.`ID` as joinid, \nmu.`MeetingID`, \nCONCAT(\'<a href=\"/meeting/?meeting_hash=\', m.`hash`, \'\"><img style=\"width:100px;\"  class=\"elv3d-meeting-logo\" src=\"\', SUBSTR(m.`logo`, 1, LOCATE(\'||\', m.`logo`)-1), \'\" /></a><br><a href=\"/meeting/?meeting_hash=\', m.`hash`, \'\">\', m.`name`, \'</a><br><span class=\"elv3d-meeting-description\">\', m.`description`, \'</span>\') as teaser,\nCONCAT(\'<span class=\"elv3d-meeeting-start\">\', m.`start`, \'</span> - <span class=\"elv3d-meeeting-end\">\', m.`end`, \'</span>\') as timing,\nm.`presenter`, \nm.`start`, \nm.`end`, \nt.`name`,\nCONCAT(\'<a href=\"/meeting/?meeting_hash=\', m.`hash`, \'\">\', m.`name`, \'</a><br><span class=\"elv3d-meeting-description\">\', m.`description`, \'</span>\') as link \nFROM `wp_elv3d_meeting_to_user` mu\nLEFT JOIN `wp_elv3d_meetings` m ON (mu.`MeetingID` = m.`id`)\nLEFT JOIN `wp_elv3d_conference_timezones` t ON (m.`timezone` = t.`id`)\nWHERE mu.`UserID` = %CURRENT_USER_ID%  AND m.`end` > (NOW() - INTERVAL t.`offset` SECOND)',0,0,1,0,1,0,0,0,'','',0,0,100,0,-1,1,1,0,1,1,'','','','a:0:{}','{\"info_block\":0,\"global_search\":1,\"showRowsPerPage\":1,\"showAllRows\":0,\"clearFilters\":0}');
